mapper.delete:
DELETE FROM
    REPLY
WHERE
    REPLY_IDX = #{REPLY_IDX}
  AND
    REPLY_BOARD_IDX = #{REPLY_BOARD_IDX}

mapper.update:
UPDATE
    REPLY
SET
    REPLY_CONTENT = {REPLY_CONTENT}
WHERE
    REPLY_BOARD_IDX = #{REPLY_BOARD_IDX}
  AND
    REPLY_IDX = #{REPLY_IDX}
  AND
    REPLY_SHOW_ID = #{REPLY_SHOW_ID}
